package Comparator;import Constant.XConstant;import java.util.ArrayList;import java.util.List;public abstract class Item {    private boolean isFile;    private final List<String> dir = new ArrayList<>();    public void store(String info) {        if(!isFile) {            dir.add(dir.size() > 1 ? XConstant.SPLIT_DIR + info : info);        } else {            dir.add("");            start();            if(compare(info)) {                output(info);            }        }    }    public void setIsFile(boolean value) {        isFile = value;    }    public void deleteDir() {        dir.remove(dir.size() - 1);    }    private String getFilePath() {        return String.join("", dir);    }    private void output(String info) {        System.out.println(getFilePath().length() == 1                ? getFilePath() + info : getFilePath() + XConstant.SPLIT_DIR + info);    }    public abstract boolean compare(String context);    public abstract void start();}