import Service.ArgumentProcess;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import static matcher.FileTestData.FILE_PATH;import static org.junit.jupiter.api.Assertions.assertThrows;import Exception.ArgumentException;public class ArgumentProcessTest {    @Test    public void ArgumentProcessFirstParameter() {        String[] args = {"-g", FILE_PATH};        ArgumentException exception = assertThrows(ArgumentException.class, () -> new ArgumentProcess(args));        Assertions.assertEquals("First parameter must be -f", exception.getMessage());    }    @Test    public void ArgumentProcessZeroParameter() {        String[] args = {};        ArgumentException exception = assertThrows(ArgumentException.class, () -> new ArgumentProcess(args));        Assertions.assertEquals("There must be at least 2 parameters", exception.getMessage());    }    @Test    public void ArgumentProcessOneParameter() {        String[] args = {"-f"};        ArgumentException exception = assertThrows(ArgumentException.class, () -> new ArgumentProcess(args));        Assertions.assertEquals("There must be at least 2 parameters", exception.getMessage());    }    @Test    public void ArgumentProcessFileExists() {        String[] args = {"-f", "test.xml"};        ArgumentException exception = assertThrows(ArgumentException.class, () -> new ArgumentProcess(args));        Assertions.assertEquals("input file doesn't exists", exception.getMessage());    }    @Test    public void ArgumentProcessThirdParameter() {        String[] args = {"-f", FILE_PATH, "-r", "file-1498940214.xhtml"};        ArgumentException exception = assertThrows(ArgumentException.class, () -> new ArgumentProcess(args));        Assertions.assertEquals("Third parameter must be -s or -S", exception.getMessage());    }    @Test    public void ArgumentProcessNumberOfParameters() {        String[] args = {"-f", FILE_PATH, "-s"};        ArgumentException exception = assertThrows(ArgumentException.class, () -> new ArgumentProcess(args));        Assertions.assertEquals("The number of parameters isn't correct", exception.getMessage());    }}